import React, {Component} from 'react';
import '../css/userlogin.css';
import {NavLink} from 'react-router-dom';
import axios from 'axios';

class UserRegistration extends Component {

    constructor() {
        super();
        this.state = {
            firstname: undefined,
            lastname: undefined,
            email: undefined,
            password: undefined,
            phone: undefined,
            address: undefined,
            success: undefined,
            error: undefined,
        }
    }

    handleChange = (e) => {
        this.setState(
            {[e.target.name]: e.target.value}
        )
    };


    registerUser = () => {
        const userData = {
            firstname: this.state.firstname,
            lastname: this.state.lastname,
            email: this.state.email,
            password: this.state.password,
            phone: this.state.phone,
            address: this.state.address
        };

        axios.post('http://localhost:5000/user/register', userData)
            .then(response => {
                this.setState({
                    success: response.data.message
                });
                setTimeout(function () {
                    window.location.assign("/login")
                }, 2000);
            })
            .catch(error => {
                this.setState({
                    error: "Something went wrong. Try again!"
                });
                console.log(error.response.data.message)

            })
    };

    render() {
        return (
            <div>
                <div className="container bg-container">
                    <div className="row">
                        <div className="col-sm-9 col-md-7 col-lg-5 mx-auto">
                            {
                                this.state.error &&
                                <div className="alert alert-danger">
                                    {this.state.error}
                                </div>
                            }
                            {
                                this.state.success &&
                                <div className="alert alert-info">
                                    {this.state.success}
                                </div>
                            }
                            <div className="usercard card-signin my-5">
                                <div className="card-body">
                                    <h5 className="card-title text-center">Register Here</h5>
                                    <form className="form-signin">
                                        <div className="form-label-group">
                                            <input type="text" id="firstname" className="form-control text-center"
                                                   name="firstname"
                                                   value={this.state.firstname} onChange={this.handleChange} autoFocus
                                                   placeholder="First Name" />
                                            <label htmlFor="firstname">First Name</label>
                                        </div>
                                        <div className="form-label-group">
                                            <input type="text" id="lastname" className="form-control text-center"
                                                   name="lastname"
                                                   value={this.state.lastname} onChange={this.handleChange}
                                                   placeholder="Last Name" autoFocus/>
                                            <label htmlFor="lastname">Last Name</label>
                                        </div>
                                        <div className="form-label-group">
                                            <input type="email" id="inputEmail" className="form-control text-center"
                                                   name="email"
                                                   value={this.state.email} onChange={this.handleChange}
                                                   placeholder="Email address" required autoFocus/>
                                            <label htmlFor="inputEmail">Email address</label>
                                        </div>
                                        <div className="form-label-group">
                                            <input type="password" id="password" className="form-control text-center"
                                                   name="password"
                                                   value={this.state.password} onChange={this.handleChange}
                                                   placeholder="Password" required autoFocus/>
                                            <label htmlFor="password">Password</label>
                                        </div>
                                        <div className="form-label-group">
                                            <input type="text" id="phone" className="form-control text-center"
                                                   name="phone"
                                                   value={this.state.phone} onChange={this.handleChange}
                                                   placeholder="Phone Number" required autoFocus/>
                                            <label htmlFor="phone">Phone Number</label>
                                        </div>
                                        <div className="form-label-group">
                                            <input type="text" id="address" className="form-control text-center"
                                                   name="address"
                                                   value={this.state.address} onChange={this.handleChange}
                                                   placeholder="Address" required autoFocus/>
                                            <label htmlFor="address">Address</label>
                                        </div>
                                        <input type="button" className="btn btn-lg btn-primary btn-block text-uppercase"
                                               value="Sign Up" onClick={this.registerUser}/>
                                        <hr className="my-line4"/>
                                        <NavLink className="btn btn-lg btn-signup btn-block text-uppercase" to="/login">Sign
                                            In</NavLink>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="container">
                    <div className="col-sm-9 col-md-7 col-lg-5 mx-auto my-block">
                        <NavLink to="/" className="btn btn-lg btn-block text-uppercase btn-back"
                                 type="submit">Home</NavLink>
                    </div>
                </div>

            </div>
        );
    }
}

export default UserRegistration;
