import React from 'react';
import axios from 'axios';
import '../css/frontend.css';

class ProductRep extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            productQuantity: this.props.pnumofitem,
            success: "",
            error: "",
            config: {
                headers: {'Authorization': `Bearer ${localStorage.getItem('token')}`}
            }
        }
    }


    handleChange = (e) => {
        this.setState(
            {[e.target.name]: e.target.value})
    };

    stepDown = (e) => {
        if (this.state.productQuantity >= 0) {
            e.preventDefault();
        }
        this.setState({productQuantity: this.state.productQuantity - 1});
    };

    stepUp = (e) => {
        e.preventDefault();
        this.setState({productQuantity: this.state.productQuantity + 1});
        // this.setState({ subtotal: parseInt(this.state.productQuantity) * parseInt(this.props.pprice)})   
    };

    deleteCartItem = (e) => {
        e.preventDefault();
        axios.delete('http://localhost:4000/remove/carts/' + this.props.cid, this.state.config)
            .then(response => {
                console.log(response.data.successmsg);
                alert(response.data.successmsg);
                window.location.reload();
            })
            .catch(error => {
                console.log(error.response);
                alert(error.response.data.errmsg);
            })
    };

    render() {
        return (
            <tr>
                <td>
                    <img className="rounded-circle cart-image"
                         src={`http://localhost:4000/productuploads/${this.props.pimage}`}
                         alt={this.props.pname}/>
                </td>
                <td className="cartdata first-cap">
                    {this.props.pname}
                </td>
                <td className="text-center cartdata">
                    {this.props.pprice}
                </td>
                <td className="cartdata">
                    <div className="def-number-input number-input safari_only">
                        <button onClick={this.stepDown} className="minus"></button>
                        <input type="number" name="productQuantity" className="quantity" min="0"
                               value={this.state.productQuantity} onChange={this.handleChange}/>
                        <button onClick={this.stepUp} className="plus"></button>
                    </div>
                </td>
                <td className="text-center cartdata">
                    {this.props.pprice * this.state.productQuantity}
                    {/* {setss(this.props.pprice)} */}


                    {/* {total = reduce((totalCalories, meal) => totalCalories + parseInt(meal.calorie, 10), 0)} */}
                </td>
                <td className="text-center cartdata">
                    <button className="btn btn-sm btn-danger" onClick={this.deleteCartItem}>Remove</button>
                </td>
            </tr>
        )

    }
}

export default ProductRep;
