import React from 'react';
import './Teacher.css'
import axios from 'axios'
import {Route, NavLink, Switch} from 'react-router-dom';
import teachersingle from './teachersingle'



class TeacherBody extends React.Component{
    DeleteTeacher = () =>{
        axios.delete("http://localhost:5000/delete/" + this.props.id);
        window.location.reload(false)
    }
    render(){
    return(
        <div className='teacherMain'>
            
        <div className='teacherEach'><h1>{this.props.name111} {this.props.lastname}</h1>
        <h2>{this.props.email}</h2>
        <h3>{this.props.gender}</h3>

        <NavLink to= {"/teachersingle/"+this.props.id}>Single</NavLink>

      
        {/* <p>He is very Helpful Teacher.</p>
        <p>This is </p> */}
        <p><button onClick={this.DeleteTeacher}>Delete</button></p>
        </div>
        
        </div>
    )
}

}

export default TeacherBody;
