import React from 'react'
import axios from 'axios';

class teachersingle extends React.Component{
    state = {
        teacher_data : []
    }

    componentDidMount(){
        axios.get("http://localhost:5000/user/single/"+this.props.match.params.id).then(res=>{
        this.setState({teacher_data : res.data})
    })
}
    render(){
    return(
            // <div> this is output of dynamic react{this.props.match.params.id} </div>    
            <div>{this.state.teacher_data.firstname}</div> 
    )
}
}


