import React, {Component} from 'react';
import {NavLink, withRouter} from 'react-router-dom';
import '../../css/adminheader.css';


class DashHeader extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user: [],
            config: {
                headers: {'Authorization': `Bearer ${localStorage.getItem('token')}`}
            }
        }
    }

    logoutHandle = (e) => {
        e.preventDefault();
        localStorage.removeItem('token');
        this.props.history.push('/login');
    };

    render() {
        return (


            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <NavLink className="navbar-brand brand-logo mr-5" to="/admindashboard">
                    <span>PrintStation Dashboard</span>
                </NavLink>
                <button className="navbar-toggler" type="button" data-toggle="collapse"
                        data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent"
                        aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarSupportedContent">
                    <ul className="navbar-nav ml-auto mt-2 mt-lg-0">
                        <li className="nav-item">
                            <NavLink to="/category" className="nav-link">
                                Category
                            </NavLink>
                        </li>
                        <li className="nav-item">
                            <NavLink to="/viewproducts" className="nav-link">
                                Products
                            </NavLink>
                        </li>
                        <li className="nav-item">
                            <NavLink to="/view/alluser" className="nav-link">
                                User List
                            </NavLink>
                        </li>
                        <li className="nav-item dropdown">
              <span className="nav-link dropdown-toggle nav-hover" id="navbarDropdown" role="button"
                    data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
              {localStorage.getItem('name')}
              </span>
                            <div className="dropdown-menu dropdown-menu-right" aria-labelledby="navbarDropdown">
                                <NavLink to={"/admin/profile/" + localStorage.getItem('id')} className="dropdown-item"
                                         href="#" id="navbarDropdown" data-toggle="dropdown" aria-haspopup="true"
                                         aria-expanded="false">
                                    Your profile
                                </NavLink>
                                <div className="dropdown-divider"></div>
                                <span className="dropdown-item nav-hover" onClick={this.logoutHandle}>Logout</span>
                            </div>
                        </li>
                    </ul>
                </div>
            </nav>

        );
    }
}

export default withRouter(DashHeader);
