import React from 'react'
import {Redirect, Route} from 'react-router-dom'

const withPrivateRoute = ({component: Component, ...rest}) => (
    <Route
        {...rest}
        render={props =>
            localStorage.getItem("token") ? (
                <Component {...props} />
            ) : (
                <Redirect to={{
                    pathname: '/',
                    state: {from: props.location}
                }}
                />
            )}
    />
);

export default withPrivateRoute;
