import React from 'react'
import axios from 'axios';

export default class Product extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            config: {
                headers: {'Authorization': `Bearer ${localStorage.getItem('token')}`}
            }
        }
    }

    deleteProduct = (e) => {
        e.preventDefault();
        axios.delete('http://localhost:5000/product/' + this.props.pid, this.state.config)
            .then(response => {
                console.log(response.data.message);
                alert(response.data.message);
                window.location.reload();
            })
            .catch(error => {
                console.log(error.message);
                alert(error.message);
            })
    };

    render() {
        const {_id, name, image, price, description, categoryId} = this.props.product;
        return (
            // <tr>
            //     <td className="py-1">
            //         <img src={`http://localhost:5000/${this.props.pimage}`} alt={this.props.pname} />
            //     </td>
            //     <td>{this.props.pname}</td>
            //     <td>Nrs. {this.props.pprice}</td>
            //     <td>{this.props.pcategory}</td>
            //     <td>{this.props.pdescription}</td>
            //     <td></td>
            // </tr>
            <div className="infoWrap">
                <div className="cartSection">
                    <img src={`http://localhost:5000/${image}`} alt={name}
                         className="itemImg"/>

                    <p className="itemNumber">#{_id}</p>
                    <h3>{name}</h3>

                    <p>NRs {price}</p>

                    <p className="stockStatus"> {categoryId}</p>
                </div>
                <div className="cartSection removeWrap">
                    <button onClick={this.deleteProduct} className="btn btn-inverse-danger btn-fw">Remove</button>
                </div>
            </div>
        )

    }
}
