import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';

import axios from "axios";
import {Redirect} from "react-router-dom";

import '../assets/auth.css';


export default class AuthScreen extends React.Component {

    constructor() {
        super();
        this.state = {
            currentView: 'login'
        };
    }

    render() {
        return (
            <div class="register">
                <div className="row">
                    <div className="col-md-3 register-left">
                        <img src="https://image.ibb.co/n7oTvU/logo_white.png" alt=""/>
                        <h3>Welcome</h3>
                        <p>You are 30 seconds away from getting customised products!</p>
                    </div>
                    <div className="col-md-9 register-right">
                        <ul className="nav nav-tabs nav-justified" id="myTab" role="tablist">
                            <li className="nav-item">
                                <a className="nav-link active" id="home-tab" data-toggle="tab" href="#register"
                                   role="tab"
                                   aria-controls="home"
                                   aria-selected={this.state.currentView === 'register'}>Registration</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" id="profile-tab" data-toggle="tab" href="#login" role="tab"
                                   aria-controls="profile" aria-selected={this.state.currentView === 'login'}>Login</a>
                            </li>
                        </ul>
                        <div className="tab-content" id="myTabContent">
                            <div className="tab-pane fade show active" id="register" role="tabpanel"
                                 aria-labelledby="home-tab">
                                <RegisterComponent onRegister={() => this.setState({currentView: 'login'})}/>
                            </div>
                            <div className="tab-pane fade show" id="login" role="tabpanel"
                                 aria-labelledby="profile-tab">
                                <LoginComponent/>
                            </div>
                        </div>
                    </div>
                </div>

            </div>
        )
    }
}


class LoginComponent extends React.Component {

    constructor() {
        super();
        this.state = {
            success: undefined,
            error: undefined,
            email: undefined,
            password: undefined,
            loggedIn: false,
            admin: false,
        }
    }

    login = () => {
        const loginData = {
            email: this.state.email,
            password: this.state.password,
        };

        axios.post(' http://localhost:5000/user/login', loginData)
            .then(response => {
                console.log(response.data);
                const {_id: id, firstname, lastname, email, phone, address, admin} = response.data.data.user;

                localStorage.setItem('token', response.data.data.token);
                localStorage.setItem('id', id);
                localStorage.setItem('name', firstname + " " + lastname);
                localStorage.setItem('firstname', firstname);
                localStorage.setItem('lastname', lastname);
                localStorage.setItem('email', email);
                localStorage.setItem('contact', phone);
                localStorage.setItem('address', address);
                localStorage.setItem('super', admin);
                this.setState({loggedIn: true, admin: admin, email: undefined, password: undefined});
            }).catch(error => {

                console.log(error.response)
            this.setState({
                error: error.response.message
            });
        })
    };

    handleChange = (e) => {
        this.setState(
            {[e.target.name]: e.target.value}
        )
    };


    render() {
        if (this.state.loggedIn === true) {
            if (this.state.admin === true) {
                return <Redirect to="/admindashboard"/>
            } else {
                return <Redirect to="/dashboard"/>
            }
        }

        return (
            <div>
                <h3 className="register-heading">Login</h3>
                <div className="row register-form">
                    <div className="col-md-4">

                    </div>
                    <div className="col-md-8">
                        {
                            this.state.error &&
                            <div className="alert alert-danger">
                                {this.state.error}
                            </div>
                        }
                        {
                            this.state.success &&
                            <div className="alert alert-info">
                                {this.state.success}
                            </div>
                        }
                        <div className="form-group"></div>
                        <div className="form-group"></div>
                        <div className="form-group"></div>
                        <div className="form-group">
                            <input type="email" className="form-control" placeholder="Email *"
                                   value={this.state.email} onChange={this.handleChange} autoFocus/>
                        </div>
                        <div className="form-group">
                            <input type="password" className="form-control" placeholder="Password *"
                                   value={this.state.password} onChange={this.handleChange} autoFocus/>
                        </div>

                        <input type="submit" className="btnLogin" value="Login" onClick={this.login}/>
                    </div>
                </div>
            </div>
        )
    }
}


class RegisterComponent extends React.Component {

    constructor() {
        super();
        this.state = {
            firstName: undefined,
            lastName: undefined,
            email: undefined,
            password: undefined,
            phone: undefined,
            address: undefined,
            success: undefined,
            error: undefined,
        }
    }

    registerUser = () => {
        const userData = {
            firstname: this.state.firstName,
            lastname: this.state.lastName,
            email: this.state.email,
            password: this.state.password,
            phone: this.state.phone,
            address: this.state.address
        };

        axios.post('http://localhost:5000/user/register', userData)
            .then(response => {
                this.setState(
                    {
                        success: response.data.message,
                        error: undefined,
                        firstName: undefined,
                        lastName: undefined,
                        address: undefined,
                        phone: undefined,
                        email: undefined,
                        password: undefined,
                    },
                    () => {
                        this.props.onRegister && this.props.onRegister();
                    }
                );
            })
            .catch(error => {
                this.setState({
                    error: "Something went wrong. Try again!"
                });
                console.log(error.response.data.message)

            })
    };


    handleChange = (e) => {
        this.setState(
            {[e.target.name]: e.target.value}
        )
    };

    render() {
        return (
            <div>
                <h3 className="register-heading">Sign up</h3>

                {
                    this.state.error &&
                    <div className="alert alert-danger">
                        {this.state.error}
                    </div>
                }
                {
                    this.state.success &&
                    <div className="alert alert-info">
                        {this.state.success}
                    </div>
                }
                <div className="row register-form">
                    <div className="col-md-6">
                        <div className="form-group">
                            <input type="text" name='firstName' className="form-control"
                                   placeholder="First Name *"
                                   value={this.state.firstName} onChange={this.handleChange} autoFocus/>
                        </div>
                        <div className="form-group">
                            <input type="text" name='lastName' className="form-control"
                                   placeholder="Last Name *"
                                   value={this.state.lastName} onChange={this.handleChange} autoFocus/>
                        </div>
                        <div className="form-group">
                            <input type="address" name='address' className="form-control"
                                   placeholder="Address *" value={this.state.address} onChange={this.handleChange}
                                   autoFocus/>
                        </div>
                        <div className="form-group">
                            <input type="text" minLength="10" maxLength="10" name="phone"
                                   className="form-control" placeholder="Your Phone Number *" value={this.state.phone}
                                   onChange={this.handleChange} autoFocus/>
                        </div>
                    </div>
                    <form className="col-md-6 form-signIn">
                        <div className="col-md-12">
                            <div className="form-group">
                                <input type="email" name='email' className="form-control"
                                       placeholder="Your Email *"
                                       value={this.state.email} onChange={this.handleChange} autoFocus/>
                            </div>
                            <div className="form-group">
                                <input type="password" name='password' className="form-control"
                                       placeholder="Password *"
                                       value={this.state.password} onChange={this.handleChange} autoFocus/>
                            </div>
                            <input type="submit" className="btnRegister" value="Register" onClick={this.registerUser}/>
                        </div>
                    </form>
                </div>
            </div>
        )
    }

}
