import React, {Component} from 'react';
import AdminHead from '../adminheader';
import axios from 'axios';

import '../../../../assets/product.css';

import Product from './product-item';

class ViewProducts extends Component {
    constructor(props) {
        super(props);
        this.state = {
            products: [],
            config: {
                headers: {'Authorization': `Bearer ${localStorage.getItem('token')}`}
            }
        }
    }

    componentDidMount() {
        axios.get('http://localhost:5000/admin/products', this.state.config)
            .then((response) => {
                this.setState({
                    products: response.data.data
                })
            });
    }

    render() {
        const adminLoopProduct = this.state.products.map((prolist, index) => {
            return <Product key={prolist._id} pid={prolist._id} pname={prolist.name} pprice={prolist.price}
                            pcategory={prolist.categoryId} pimage={prolist.image} pdescription={prolist.description}/>
        });
        return (
            <div>
                <AdminHead/>
                <div className="container-fluid content-wrapper">
                    <div className="row">
                        <div className="wrap cf">
                            <h1 className="projTitle">Products</h1>
                            <div className="cart">
                                <ul className="cartWrap">
                                    {
                                        (this.state.products || []).map(item => {
                                            return <li className="items">
                                                <Product key={item._id} product={item}/>
                                            </li>
                                        })
                                    }
                                </ul>
                            </div>
                        </div>
                    </div>
                    <footer className="footer">
                        <div className="d-sm-flex justify-content-center justify-content-sm-between">
                        <span className="text-muted text-center text-sm-left d-block d-sm-inline-block">
                            Copyright Â© 2020 Grocery Store Shopping.</span>
                            <span className="float-none float-sm-right d-block mt-1 mt-sm-0 text-center">
                            Admin Panel | React Basics</span>
                        </div>
                    </footer>
                </div>
            </div>


        );
    }
}

export default ViewProducts;
