import React, {Component} from 'react';
import axios from 'axios';

class TeacherLogin extends Component{
    constructor(props)
    {
        super(props);
        this.state = 
        {
        name : "",
        password : ""   
        }
    login = () =>{
        userdata = {
            username : this.state.username,
            password : this.state.password
                    }   
                      
    
        axios.post("http://localhost:5000/login/", userdata).then((Response) => {

        localStorage.setItem('token', Response.data.token)
    
    }).catch()
}
    
}

    render() {
        return(

    <div>
       
        <p><input type= "text" value={this.state.username} onChange={(event)=>this.setState({username:event.target.value})}></input></p>
        <p><input type="password" value= {this.state.password} onChange = {(event)=>this.setState({password:event.target.value})}></input></p>
        <button onClick={this.Login} value="submit"></button>
        
    </div>
    
        );
    }
}
export default TeacherLogin;
