import React from 'react';
import axios from 'axios';
import '../css/frontend.css';

class ProductRep extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            product: props.product,
            success: "",
            error: "",
            config: {
                headers: {'Authorization': `Bearer ${localStorage.getItem('token')}`}
            }
        }
    }


    handleChange = (e) => {
        this.setState(
            {[e.target.name]: e.target.value})
    };

    saveProduct(product) {
        let wishList = [];
        try {
            wishList = JSON.parse(localStorage.getItem('wishlist')) || [];
        } catch (e) {
        }
        wishList = wishList.filter(item => {
            const isSelectedItem = item.productId === product.productId;
            if (isSelectedItem) {
                product.quantity += item.quantity;
            }
            return !isSelectedItem;
        });
        wishList.push(product);
        localStorage.setItem('wishlist', JSON.stringify(wishList));
    }

    stepDown = (e) => {
        e.preventDefault();
        if (this.state.productQuantity >= 0) {
            return;
        }

        let product = this.state.product;
        product.quantity = product.quantity - 1;
        this.saveProduct(product);
        this.setState(product);
    };

    stepUp = (e) => {
        e.preventDefault();

        let product = this.state.product;
        product.quantity = product.quantity + 1;
        this.saveProduct(product);
        this.setState(product);
    };

    deleteCartItem = (e) => {
        e.preventDefault();
        axios.delete('http://localhost:5000/remove/carts/' + this.props.cid, this.state.config)
            .then(response => {
                console.log(response.data.message);
                alert(response.data.message);
                window.location.reload();
            })
            .catch(error => {
                console.log(error.response);
                alert(error.response.data.message);
            })
    };

    render() {
        const {name, image, price, quantity} = this.state.product || {};
        return (
            <tr>
                <td>
                    <img className="rounded-circle cart-image"
                         src={`http://localhost:4000/productuploads/${image}`}
                         alt={name}/>
                </td>
                <td className="cartdata first-cap">
                    {name}
                </td>
                <td className="text-center cartdata">
                    {price}
                </td>
                <td className="cartdata">
                    <div className="def-number-input number-input safari_only">
                        <button onClick={this.stepDown} className="minus"></button>
                        <input type="number" name="productQuantity" className="quantity" min="0"
                               value={quantity} onChange={this.handleChange}/>
                        <button onClick={this.stepUp} className="plus"></button>
                    </div>
                </td>
                <td className="text-center cartdata">
                    {price * quantity}
                </td>
                <td className="text-center cartdata">
                    <button className="btn btn-sm btn-danger" onClick={this.deleteCartItem}>Remove</button>
                </td>
            </tr>
        )

    }
}

export default ProductRep;
