import React, {Component} from 'react';
import '../css/frontend.css';
import CartLoopData from './cartloopdata';
import {NavLink} from 'react-router-dom';

import axios from 'axios';
import OrdersComponent from "../dashboard/admin/orders";

class CartBody extends Component {
    constructor(props) {
        super(props);

        let wishLists = this.loadWishList();

        this.state = {
            wishLists,
            orders: [],
            userId: localStorage.getItem('id'),
            success: undefined,
            error: undefined,
            config: {
                headers: {'Authorization': `Bearer ${localStorage.getItem('token')}`}
            }
        }
    }

    componentDidMount() {
        axios.get('http://localhost:5000/orders', this.state.config)
            .then((response) => {
                this.setState({orders: response.data.data})
            });

    }

    loadWishList() {
        try {
            return JSON.parse(localStorage.getItem('wishlist')) || [];
        } catch (e) {
        }
        return [];
    }

    handleChange = (e) => {
        this.setState(
            {[e.target.name]: e.target.value}
        )
    };

    checkout = () => {
        let orders = [];
        this.state.wishLists.forEach(item => {
            orders.push({
                productId: item.productId,
                quantity: item.quantity
            })
        });
        axios.post('http://localhost:5000/product/order', {orders}, this.state.config)
            .then((response) => {
                localStorage.setItem('wishlist', JSON.stringify([]));
                this.setState({
                    wishLists: [],
                    success: response.data.message,
                    error: undefined
                });
            }).catch(error => {
            this.setState({
                error: (error.response && error.response.message) || "Something went wrong, please try again",
                success: undefined
            })
        });
    };

    render() {
        const wishListItems = this.state.wishLists.map((item, index) => {
            return <CartLoopData key={item._id} cid={item._id} product={item} onChange={() => {
                let wishLists = this.loadWishList();
                this.setState({wishLists})
            }
            }/>
        });
        let totalPrice = 0;
        this.state.wishLists.forEach(item => {
            totalPrice += item.price * item.quantity;
        });

        const myOrders = this.state.orders.map((order, index) => {
            return <OrdersComponent key={order._id} order={order}/>
        });

        return (
            <div className="cart-body">
                {
                    this.state.success &&
                    <div className="alert alert-info" style={{display: this.state.success !== "" ? "" : "none"}}>
                        {this.state.success}
                    </div>
                }
                {
                    this.state.error &&
                    <div className="alert alert-danger" style={{display: this.state.error !== "" ? "" : "none"}}>
                        {this.state.error}
                    </div>
                }
                <div className="container mb-4">
                    <h2 className="mb-5 first-cap">Personal Cart</h2>

                    <div className="row">
                        <div className="col-12">
                            <div className="table-responsive">
                                <table className="table table-striped table-bordered">
                                    <thead className="thead-dark">
                                    <tr>
                                        <th scope="col">Image</th>
                                        <th scope="col">Name</th>
                                        <th scope="col">Product Price</th>
                                        <th scope="col" className="text-center">Quantity</th>
                                        <th scope="col" className="text-center">Amount</th>
                                        <th>Action</th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                    {wishListItems}
                                    <tr>
                                        <td colSpan="4"></td>
                                        <td><strong>Total</strong></td>
                                        <td className="text-center"><strong>NPR {totalPrice}</strong></td>
                                    </tr>
                                    </tbody>
                                </table>
                            </div>
                        </div>
                        <div className="col mb-2">
                            <div className="row">
                                <div className="col-sm-12  col-md-6">
                                    <NavLink to="/products" className="btn btn-lg btn-block btn-light text-uppercase">Continue
                                        Shopping</NavLink>
                                </div>
                                <div className="col-sm-12 col-md-6 text-right">
                                    <button className="btn btn-lg btn-block btn-info text-uppercase"
                                            onClick={this.checkout}>Checkout
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>


                <div className="container mb-4">
                    <h2 className="mb-5 first-cap">My Orders</h2>

                    <div className="row">
                        <div className="col-12">
                            <div className="table-responsive">
                                <table className="table table-striped table-bordered">
                                    <thead className="thead-dark">
                                    <tr>
                                        <th></th>
                                        <th>Product</th>
                                        <th>Quantity</th>
                                        <th>Total Price</th>
                                        <th>Status</th>
                                        <th>Action</th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                    {this.state.orders.map(order => {
                                        const {name, price, image} = order.product || {};

                                        return <tr className="alert alert-info"
                                                   style={{display: this.state.success !== "" ? "" : "none"}}>
                                            <td colSpan="6">{this.state.success}</td>
                                        </tr>,
                                            <tr className="alert alert-danger"
                                                style={{display: this.state.error !== "" ? "" : "none"}}>
                                                <td colSpan="6">{this.state.error}</td>
                                            </tr>,
                                            <tr>
                                                <td className="first-cap">
                                                    <img src={`http://localhost:5000/${image}`} alt={name}/>
                                                </td>
                                                <td className="first-cap">{name}</td>
                                                <td className="first-cap"> {order.quantity} <i
                                                    className="ti-arrow-down"></i></td>
                                                <td className="first-cap"> {order.quantity * price} <i
                                                    className="ti-arrow-down"></i></td>
                                                <td><label className="badge badge-success">{order.status}</label></td>
                                                <td><label onClick={this.removeResponse}
                                                           className="btn btn-danger">Remove</label></td>
                                            </tr>
                                    })}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default CartBody;
