import React, {Component} from 'react'
import axios from 'axios';

class TeacherLogin extends Component{
    constructor(props)
    {
        super(props);
        this.state = {
        username : '',
        password : ''   
    }
    Login = () =>{
        userdata = {
            username : this.state.username,
            password : this.state.password
        }}
    
        axios.post("http://localhost:5000/login/", userdata).then((Response) => {
        localStorage.setItem('token',Response.data.token)
    
    }).catch()
    
}

    render() {
        return(

    <div>
       
        <p><input type= "text" valure={this.state.username} onchage={(event)=>this.state.usernameHandle}></input></p>
        <p><input type="password" value= {this.state.password} onChange = {(event)=>this.state.passwordHandle}></input></p>
        <button onClick={this.Login} value="submit"></button>
        
    </div>
    
        );
}
}

export default TeacherLogin;
