import React, {Component} from 'react';
import AdminHead from '../adminheader';
import axios from 'axios';
import '../../../css/adminpanel.css';
import {NavLink} from 'react-router-dom';

class UpdateCategory extends Component {
    constructor() {
        super();
        this.state = {
            category: undefined,
            name: "",
            success: "",
            error: "",
            config: {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            }
        };
    }

    componentDidMount() {
        let id = this.props.match.params.id;
        axios.get("http://localhost:5000/category/" + id, {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        }).then(result => {
            this.setState({category: result.data.data})
        }).catch(error => {
            console.log("category get", error);
        })
    }

    handleChange = (e) => {
        this.setState(
            {[e.target.name]: e.target.value}
        )
    };

    updateCategory = (e) => {
        e.preventDefault();
        const {name} = this.state;

        let category = {category: name};

        axios.put('http://localhost:5000/category', category, this.state.config)
            .then(response => {
                console.log(response.data.message);
                this.setState({
                    success: response.data.message
                });
                setTimeout(function () {
                    window.location.reload()
                }, 3000);
            })
            .catch(error => {
                this.setState({
                    error: "Something went wrong. Try again!"
                });
                console.log(error.message)
            })
    };


    render() {
        let name = (this.state.category && this.state.category.category);
        name = name === undefined ? this.state.name : name;
        return (
            <div className="container-scroller" id="page-content-wrapper">
                <AdminHead/>
                <div className="container-fluid content-wrapper">
                    <div className="row">
                        <div className="col-md-12 grid-margin">
                            <div className="d-flex justify-content-between align-items-center">
                                <div>
                                    <h4 className="font-weight-bold navbar-brand brand-logo">Update
                                        Category {this.state.success}</h4>
                                </div>

                                <div>
                                    <NavLink to="/category" className="btn btn-primary btn-icon-text btn-rounded">Category
                                        List</NavLink>
                                </div>
                            </div>
                        </div>
                        <div className="col-md-12 grid-margin stretch-card">
                            <div className="card position-relative">
                                <div className="card-body">
                                    <h4 className="card-title">Enter Category Details</h4>
                                    <div className="alert alert-info"
                                         style={{display: this.state.success !== "" ? "" : "none"}}>
                                        {this.state.success}
                                    </div>
                                    <div className="alert alert-danger"
                                         style={{display: this.state.error !== "" ? "" : "none"}}>
                                        {this.state.error}
                                    </div>
                                    <form className="forms-sample">
                                        <div className="form-group">
                                            <label htmlFor="name">Name</label>
                                            <input type="text" className="form-control" name="name"
                                                   value={name}
                                                   onChange={this.handleChange}
                                                   id="name" placeholder="Name" autoCapitalize="true" autoFocus/>
                                        </div>
                                        <input type="submit" onClick={this.updateCategory}
                                               className="btn btn-primary mr-2 float-left"/>

                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <footer className="footer">
                    <div className="d-sm-flex justify-content-center justify-content-sm-between">
                        <span className="text-muted text-center text-sm-left d-block d-sm-inline-block">
                            Copyright © 2020 Grocery Store Shopping.</span>
                        <span className="float-none float-sm-right d-block mt-1 mt-sm-0 text-center">
                            Admin Panel | React Basics</span>
                    </div>
                </footer>
            </div>
        );
    }
}

export default UpdateCategory;
