import React from 'react';

class ProductRep extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            productid: this.props.pid,
            productname: this.props.pname,
            productprice: this.props.pprice,
            productcategory: this.props.pcategory,
            productimage: this.props.pimage,
            addedbyName: localStorage.getItem('name'),
            addedbyID: localStorage.getItem('id'),
            success: "",
            error: "",
            productQuantity: 0,
            config: {
                headers: {'Authorization': `Bearer ${localStorage.getItem('token')}`}
            }
        }
    }

    handleChange = (e) => {
        this.setState(
            {[e.target.name]: e.target.value}
        )
    };

    stepDown = (e) => {
        e.preventDefault();
        if (this.state.productQuantity <= 0) {
            return;
        }
        this.setState({productQuantity: this.state.productQuantity - 1});
    };

    stepUp = (e) => {
        e.preventDefault();
        this.setState({productQuantity: this.state.productQuantity + 1});
    };

    cartAdd = (e) => {
        e.preventDefault();
        if (this.state.productQuantity <= 0) {
            this.setState({
                error: "Please Choose Product Quantity first"
            });
            setTimeout(function () {
                window.location.reload()
            }, 1000);
        } else {
            let product = {
                productId: this.state.productid,
                name: this.state.productname,
                price: this.state.productprice,
                category: this.state.productcategory,
                quantity: this.state.productQuantity,
                orderedBy: this.state.addedbyName,
                userId: this.state.addedbyID,
                image: this.state.productimage
            };
            let wishList = [];
            try {
                wishList = JSON.parse(localStorage.getItem('wishlist')) || [];
            } catch (e) {
            }
            wishList = wishList.filter(item => {
                const isSelectedItem = item.productId === product.productId;
                if (isSelectedItem) {
                    product.quantity += item.quantity;
                }
                return !isSelectedItem;
            });
            wishList.push(product);
            localStorage.setItem('wishlist', JSON.stringify(wishList));
            this.setState({success: "Product added to cart", error: ""}, () => {
                setTimeout(function () {
                    window.location.reload()
                }, 1000);
            });
        }
    };

    render() {
        return (
            <div className="col-lg-4">
                <div className="alert alert-info" style={{display: this.state.success !== "" ? "" : "none"}}>
                    {this.state.success}
                </div>
                <div className="alert alert-danger" style={{display: this.state.error !== "" ? "" : "none"}}>
                    {this.state.error}
                </div>
                <div className="testimonial-item mx-auto mb-5 mb-lg-0">
                    <img className="img-fluid mb-3"
                         src={`http://localhost:5000/${this.props.pimage}`} alt={this.props.pname}/>
                    <h5>{this.props.pname} | Nrs.{this.props.pprice} {this.state.categortrec} </h5>
                    <p className="font-weight-light mb-0">{this.props.pdescription}</p>
                    <div className="font-weight-light mb-0">
                        <form>
                            <div className="def-number-input number-input safari_only">
                                <button onClick={this.stepDown} className="minus"></button>
                                <input type="number" name="productQuantity" className="quantity" min="0"
                                       value={this.state.productQuantity} onChange={this.handleChange}/>
                                <button onClick={this.stepUp} className="plus"></button>
                            </div>
                            <input type="submit" className="btn btn-primary btn-cart" onClick={this.cartAdd}
                                   value="Add to cart"/>
                        </form>
                    </div>
                </div>
            </div>
        )

    }
}

export default ProductRep;
