import React, {Component} from 'react';
import '../../css/frontend.css';
import axios from 'axios';

class OrdersComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            orderStatus: undefined,
            success: "",
            error: "",
            config: {
                headers: {'Authorization': `Bearer ${localStorage.getItem('token')}`}
            }
        }
    }

    handleChange = (e) => {
        this.setState(
            {[e.target.name]: e.target.value}
        )
    };

    updateOrderStatus = (e) => {
        e.preventDefault();
        if (this.state.orderStatus === undefined) return;

        const requestData = {
            orderId: this.props.order._id,
            status: this.state.orderStatus
        };
        axios.post('http://localhost:5000/order/status', requestData, this.state.config)
            .then(response => {
                this.setState({
                    success: response.data.message,
                    error: undefined
                }, ()=>{
                    setTimeout(function () {
                        window.location.reload();
                    }, 1000);
                });
            })
            .catch(error => {
                this.setState({
                    error: "Could not update the order status"
                });
            })
    };

    loadAdminItem() {
        const {order} = this.props;
        const {name, price, image} = order.product || {};
        const {firstname, lastname, phone} = order.user || {};

        let orderStatus = [];
        const status = (order.status || "").toLowerCase();

        if (status === 'pending') {
            orderStatus = ['proceed'];
        } else if (status === 'proceed') {
            orderStatus = ['delivered'];
        }


        return <tr>
            <td className="first-cap">
                <img src={`http://localhost:5000/${image}`} alt={name}/>
            </td>
            <td className="first-cap">{name}</td>
            <td className="first-cap">{firstname + " " + lastname}</td>
            <td className="first-cap"> {order.quantity} <i className="ti-arrow-down"></i></td>
            <td className="first-cap"> {order.quantity * price} <i className="ti-arrow-down"></i></td>
            <td><label className="badge badge-success">{order.status}</label></td>

            {
                status !== 'delivered' && <td><label className="btn btn-primary" data-toggle="modal" data-target={"#exampleModalCenter"+ order._id}>Respond</label></td>
            }
            <td className="modal fade" id={"exampleModalCenter"+ order._id} tabIndex="-1" role="dialog"
                aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
                <div className="modal-dialog modal-dialog-centered" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title first-cap" id="exampleModalLongTitle">Response
                                for {firstname + " " + lastname}</h5>
                            <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        {
                            this.state.success &&
                            <div className="alert alert-info">
                                {this.state.success}
                            </div>
                        }
                        {
                            this.state.error &&
                            <div className="alert alert-danger">
                                {this.state.error}
                            </div>
                        }

                        <div className="modal-body">
                            <form className="forms-sample">
                                <div className="form-group">
                                    <label htmlFor="status">Status</label>
                                    <select value={this.state.orderStatus} className="form-control" name="orderStatus"
                                            id={"status"+ order._id} onChange={this.handleChange}>
                                        <option value={undefined}>Select Status</option>
                                        {
                                            orderStatus.map(status => {
                                                return <option key={status} value={status}>{status}</option>;
                                            })
                                        }
                                    </select>
                                </div>
                            </form>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Close
                            </button>
                            <button type="submit" onClick={this.updateOrderStatus} className="btn btn-primary">
                                send Response
                            </button>
                        </div>
                    </div>
                </div>
            </td>
        </tr>
    }

    loadUserItem() {
        const {order} = this.props;
        const {name, price, description, image} = order.product || {};
        const {firstname, lastname, phone} = order.user || {};

        return [
            <tr className="alert alert-info" style={{display: this.state.success !== "" ? "" : "none"}}>
                <td colSpan="6">{this.state.success}</td>
            </tr>,
            <tr className="alert alert-danger" style={{display: this.state.error !== "" ? "" : "none"}}>
                <td colSpan="6">{this.state.error}</td>
            </tr>,
            <tr>
                <td className="first-cap">
                    <img src={`http://localhost:5000/${image}`} alt={name}/>
                </td>
                <td className="first-cap">{name}</td>
                <td className="first-cap">{firstname + " " + lastname}</td>
                <td className="first-cap"> {order.quantity} <i className="ti-arrow-down"></i></td>
                <td className="first-cap"> {order.quantity * price} <i className="ti-arrow-down"></i></td>
                <td><label className="badge badge-success">{order.status}</label></td>
                <td><label onClick={this.removeResponse} className="btn btn-danger">Remove</label></td>
            </tr>
        ]

        return (
            <div className="product">
                <div className="img-container">
                    <img src={`http://localhost:5000/${image}`} alt={name}/>
                </div>
                <div className="product-info">
                    <div className="product-content">
                        <h1>{name}</h1>
                        <p></p>
                        <ul>
                            <li>Lorem ipsum dolor sit ametconsectetu.</li>
                            <li>adipisicing elit dlanditiis quis ip.</li>
                            <li>lorem sde glanditiis dars fao.</li>
                        </ul>
                        <div className="buttons">
                            <a className="button buy" href="#">Buy</a>
                            <a className="button add" href="#">Add to Cart</a>
                            <span className="button" id="price">$59,99</span>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    render() {
        return (
            <tbody>
            {this.props.admin ? this.loadAdminItem() : this.loadUserItem() }
            </tbody>
        );
    }
}

export default OrdersComponent;
