import React, {Component} from 'react';
import '../css/frontend.css';
import CartLoopData from './cartloopdata';
import {NavLink} from 'react-router-dom';

class CartBody extends Component {
    constructor(props) {
        super(props);

        let wishLists = [];
        try {
            wishLists = JSON.parse(localStorage.getItem('wishlist'));
        } catch (e) {
        }

        this.state = {
            wishLists,
            userId: localStorage.getItem('id'),
            config: {
                headers: {'Authorization': `Bearer ${localStorage.getItem('token')}`}
            }
        }
    }

    handleChange = (e) => {
        this.setState(
            {[e.target.name]: e.target.value}
        )
    };


    render() {
        const wishListItems = this.state.wishLists.map((item, index) => {
            return <CartLoopData key={item._id} cid={item._id} product={item}/>
        });
        return (
            <div className="cart-body">
                <div className="container mb-4">
                    <h2 className="mb-5 first-cap">Personal Cart</h2>
                    <div className="row">
                        <div className="col-12">
                            <div className="table-responsive">
                                <table className="table table-striped table-bordered">
                                    <thead className="thead-dark">
                                    <tr>
                                        <th scope="col">Image</th>
                                        <th scope="col">Name</th>
                                        <th scope="col">Product Price</th>
                                        <th scope="col" className="text-center">Quantity</th>
                                        <th scope="col" className="text-center">Amount</th>
                                        <th>Action</th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                    {wishListItems}
                                    <tr>
                                        <td colSpan="4"></td>
                                        <td>Sub-Total</td>
                                        <td className="text-center">255,90 €</td>
                                    </tr>
                                    <tr>
                                        <td colSpan="4"></td>
                                        <td>Shipping</td>
                                        <td className="text-center">6,90 €</td>
                                    </tr>
                                    <tr>
                                        <td colSpan="4"></td>
                                        <td><strong>Total</strong></td>
                                        <td className="text-center"><strong>346,90 €</strong></td>
                                    </tr>
                                    </tbody>
                                </table>
                            </div>
                        </div>
                        <div className="col mb-2">
                            <div className="row">
                                <div className="col-sm-12  col-md-6">
                                    <NavLink to="/products" className="btn btn-lg btn-block btn-light text-uppercase">Continue
                                        Shopping</NavLink>
                                </div>
                                <div className="col-sm-12 col-md-6 text-right">
                                    <button className="btn btn-lg btn-block btn-info text-uppercase">Checkout</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                {/* content */}
            </div>
        );
    }
}

export default CartBody;
